import React from "react";
import { useState } from "react";

const TextForm = (props) => {
  const [text, setText] = useState("");

  const handleUpClick = () => {
    console.log("uppper case was clicked " + text);

    let newText = text.toUpperCase();
    setText(newText);
    if (props.showAlert) {
      props.showAlert("Converted to Upper Case", "success");
    }
  };

  const handleLoClick = () => {
    console.log("lower case was clicked " + text);

    let newText = text.toLowerCase();
    setText(newText);
    if (props.showAlert) {
      props.showAlert("Converted to Lower Case", "success");
    }
  };

  const handleELPClick = () => {
    console.log("Each Letter Uppercase Clicked");
    let newText = text.split(" ").map((word) => word.charAt(0).toUpperCase() + word.slice(1)).join(" ");
    setText(newText);
    if (props.showAlert) {
      props.showAlert("Converted first letter in each word to Upper Case", "success");
    }
  };

  return (
    <>
      <div className="container" style={{ color: props.mode === 'dark' ? 'white' : 'black' }}>
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <textarea
            className="form-control"
            id="myBox"
            value={text}
            onChange={(event) => setText(event.target.value)}
            style={{ backgroundColor: props.mode === 'dark' ? 'grey' : 'white', color: props.mode === 'dark' ? 'white' : 'black' }}
            rows="8"
          ></textarea>
        </div>
        <button className="btn btn-primary mx-2" onClick={handleUpClick}>
          Convert to Uppercase
        </button>
        <button className="btn btn-primary mx-2" onClick={handleLoClick}>
          Convert to Lowercase
        </button>
        <button className="btn btn-primary mx-2" onClick={handleELPClick}>
          Each Letter Uppercase
        </button>
      </div>
    </>
  );
};

export default TextForm;
